import React from "react";

function Cart({ cart, removefromCart }) {
  const total = cart.reduce((acc, item) => acc + item.price, 0);

  return (
    <div
      style={{
        width: "320px",
        background: "white",
        padding: "20px",
        borderRadius: "15px",
        boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
        height: "fit-content",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>ðŸ›’ Your Cart</h2>

      {cart.length === 0 ? (
        <p style={{ textAlign: "center", color: "gray" }}>Cart is empty</p>
      ) : (
        cart.map((item) => (
          <div
            key={item.id}
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "15px",
              borderBottom: "1px solid #ddd",
              paddingBottom: "10px",
            }}
          >
            <img
              src={item.image}
              alt={item.name}
              style={{
                width: "60px",
                height: "60px",
                borderRadius: "8px",
                marginRight: "10px",
                objectFit: "cover",
              }}
            />

            <div style={{ flexGrow: 1 }}>
              <h4 style={{ margin: "0 0 5px 0" }}>{item.name}</h4>
              <p style={{ margin: "0", color: "#555" }}>â‚¹{item.price}</p>
            </div>

            <button
              onClick={() => removefromCart(item.id)}
              style={{
                background: "red",
                color: "white",
                border: "none",
                padding: "6px 10px",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Remove
            </button>
          </div>
        ))
      )}

      {/* Total Section */}
      {cart.length > 0 && (
        <div
          style={{
            marginTop: "20px",
            fontSize: "18px",
            fontWeight: "bold",
            textAlign: "center",
          }}
        >
          Total: â‚¹{total}
        </div>
      )}
    </div>
  );
}

export default Cart;
