import React, { useState } from "react";
import ProductList from "./components/ProductList";
import Cart from "./components/Cart";
import { products } from "./products";   // ‚≠ê Import products

function App() {
  const [cart, setCart] = useState([]);
  const [search, setSearch] = useState("");  // ‚≠ê New search state

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // ‚≠ê Filter products based on search text
  const filteredProducts = products.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center" }}>üõçÔ∏è My Shopping App</h1>

      {/* ‚≠ê Search box */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search product..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px",
            width: "300px",
            borderRadius: "5px",
            border: "1px solid gray"
          }}
        />
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          marginTop: "20px"
        }}
      >
        {/* ‚≠ê Send filtered products to ProductList */}
        <ProductList products={filteredProducts} addToCart={addToCart} />

        <Cart cart={cart} removeFromCart={removeFromCart} />
      </div>
    </div>
  );
}

export default App;
